import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// Fix for default marker icons in Leaflet with React
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

interface TransportMetric {
  line_id: string;
  line_name: string;
  status: string;
  delay_minutes: number;
  reliability_percentage: number;
  disruptions: number;
  congestion_level: number;
  timestamp: string;
}

// Mock vehicle locations for each line
const mockLocations: { [key: string]: { lat: number; lng: number } } = {
  'bakerloo': { lat: 51.5320, lng: -0.1053 },
  'central': { lat: 51.5154, lng: -0.1755 },
  'circle': { lat: 51.5120, lng: -0.1032 },
  'district': { lat: 51.5074, lng: -0.1278 },
  'hammersmith-city': { lat: 51.5194, lng: -0.1770 },
  'jubilee': { lat: 51.5033, lng: -0.1145 },
  'metropolitan': { lat: 51.5206, lng: -0.1050 },
  'northern': { lat: 51.5074, lng: -0.1278 },
  'piccadilly': { lat: 51.5098, lng: -0.1337 },
  'victoria': { lat: 51.4965, lng: -0.1447 },
  'waterloo-city': { lat: 51.5033, lng: -0.1145 }
};

const Map: React.FC = () => {
  const [metrics, setMetrics] = useState<TransportMetric[]>([]);
  const [center] = useState<[number, number]>([51.505, -0.09]); // London coordinates

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get('/metrics.json');
        setMetrics(response.data);
      } catch (error) {
        console.error('Error fetching metrics:', error);
      }
    };

    fetchMetrics();
    const interval = setInterval(fetchMetrics, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <MapContainer
      center={center}
      zoom={11}
      style={{ height: '100%', width: '100%' }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {metrics.map((metric) => {
        const location = mockLocations[metric.line_id];
        if (!location) return null;
        
        return (
          <Marker
            key={metric.line_id}
            position={[location.lat, location.lng]}
          >
            <Popup>
              <div>
                <strong>Line:</strong> {metric.line_name}<br />
                <strong>Status:</strong> {metric.status}<br />
                <strong>Delay:</strong> {metric.delay_minutes} minutes<br />
                <strong>Reliability:</strong> {metric.reliability_percentage}%<br />
                <strong>Disruptions:</strong> {metric.disruptions}
              </div>
            </Popup>
          </Marker>
        );
      })}
    </MapContainer>
  );
};

export default Map;
