import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Box } from '@mui/material';

// Fix for default marker icons in Leaflet with React
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Define start and end points for the bus route
const START_POINT = { lat: 51.5074, lng: -0.1278 }; // Victoria Station
const END_POINT = { lat: 51.5154, lng: -0.1755 };   // Oxford Circus

const Map: React.FC = () => {
  const [busPosition, setBusPosition] = useState(START_POINT);
  const [center] = useState<[number, number]>([51.505, -0.09]); // London coordinates

  useEffect(() => {
    let progress = 0;
    const interval = setInterval(() => {
      progress += 0.01;
      if (progress >= 1) {
        progress = 0;
      }

      // Calculate new position using linear interpolation
      const newLat = START_POINT.lat + (END_POINT.lat - START_POINT.lat) * progress;
      const newLng = START_POINT.lng + (END_POINT.lng - START_POINT.lng) * progress;

      setBusPosition({ lat: newLat, lng: newLng });
    }, 100); // Update every 100ms

    return () => clearInterval(interval);
  }, []);

  return (
    <Box sx={{ height: '500px', width: '100%', position: 'relative' }}>
      <MapContainer
        center={center}
        zoom={13}
        style={{ height: '100%', width: '100%', position: 'absolute', top: 0, left: 0 }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <Marker position={[busPosition.lat, busPosition.lng]}>
          <Popup>
            <div>
              <strong>Bus 38</strong><br />
              <strong>Route:</strong> Victoria â†’ Oxford Circus<br />
              <strong>Status:</strong> In Service
            </div>
          </Popup>
        </Marker>
      </MapContainer>
    </Box>
  );
};

export default Map;
